import React from 'react';
import Typed from 'typed.js';

class ChangingText extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    const options = {
      strings: texts,
      typeSpeed: 50,
      backSpeed: 50,
      loop: true,
      backDelay: 4000,
      showCursor: false
    }

    this.typed = new Typed(this.el, options);
  }

  componentWillMount() {
    clearInterval(this.timerID);
  }

  changeText() {
    const current = {
      total: this.state.total,
      index: this.state.index,
    }

    if(current.index < current.total - 1) {
      this.setState({
        index: current.index + 1
      });
    } else {
      this.setState({
        index: 0
      });
    }
  }

  render() {
    const index = this.state.index;

    return(
      <div className="Info-occu">
        {this.props.occus[index].title}
      </div>
    );
  }
}

export default ChangingText;
