import React from 'react';
import logo from './logo.svg';
import './Nav.css';

import {
  Link
} from 'react-router-dom';

const menus = [
  {id: 1, title: 'Lorem', link: '/'},
  {id: 2, title: 'Ipsum', link: '/ipsum'},
  {id: 3, title: 'Dolor', link: '/dolor'},
  {id: 4, title: 'Sit Amet', link: '/sit-amet'}
]

class NavLeft extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: 1
    };
  }

  setActiveNav() {
    const page = this.props.page;

  }

  render() {

    const menu = this.props.menus.map((menu) => {
      return this.state.page == menu.id ?
        <Link key={menu.id}
          className="Nav-item active"
          to={menu.link}
          onClick={() => this.setActiveNav(menu.id)}>
          {menu.title}
        </Link>
	:
	<Link key={menu.id}
          className="Nav-item" 
          to={menu.link}
          onClick={this.setActiveNav}>
          {menu.title}
        </Link>
    });

    return (
      <div className="Nav-left">
        {menu}
      </div>
    );
  }
}

function NavRight() {
  return (
    <div className="Nav-right">
      <Link className="Nav-brand"
        to="/" >
        Lorem Ipsum
      </Link>
    </div>
  );
}

class Nav extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      page: 1
    };
  }

  render() {
    return (
      <nav className="Nav">
        <NavLeft menus={menus}/>
        <NavRight />
      </nav>
    );
  }
}

export default Nav;
