import React from 'react';
import './styles/Nav.css';

import {
  Link
} from 'react-router-dom';

const menus = [
  {id: 1, title: 'Home', link: '/'},
  {id: 2, title: 'Experience', link: '/experience'},
  {id: 3, title: 'Skill', link: '/skill'},
  {id: 4, title: 'About', link: '/about'},
]

class NavLeft extends React.Component {
  constructor(props) {
    super(props);
  }

  onClickHandler(id) {
    this.props.onPageChange(id);
  }

  render() {
    const menuList = this.props.menus.map((menu) => {
      console.log(menu.id);
      return this.props.page == menu.id ?
        <Link key={menu.id}
          className="Nav-item active"
          to={menu.link}>
          {menu.title}
        </Link>
	:
	<Link key={menu.id}
          className="Nav-item" 
          to={menu.link}>
          {menu.title}
        </Link>
    });

    return (
      <div className="Nav-left">
	{menuList}
	<a className="Nav-item"
	  href="https://blog.sedikitacak.com">
	  Blog
	</a>
      </div>
    );
  }
}

function NavRight() {
  return (
    <div className="Nav-right">
      <Link className="Nav-brand"
        to="/" >
        Sedikit Acak
      </Link>
    </div>
  );
}

class Nav extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      page: 1
    };
  }

  render() {
    const page = this.state.page;

    return (
      <nav className="Nav">
        <NavLeft menus={menus}
	  currentPage={page} />
        <NavRight />
      </nav>
    );
  }
}

export default Nav;
