import React from 'react';

class ChangingText extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      total: props.occus.length,
      index: 0
    }
  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.changeText(),
      4000
    );
  }

  componentWillMount() {
    clearInterval(this.timerID);
  }

  changeText() {
    const current = {
      total: this.state.total,
      index: this.state.index,
    }

    if(current.index < current.total) {
      this.setState({
        index: current.index + 1
      });
    } else {
      this.setState({
        index: 0
      });
    }
  }

  render() {
    const index = this.state.index;

    return(
      <div className="Info-occu">
        {this.props.occus[index].title};
      </div>
    );
  }
}

export default ChangingText;
